document.addEventListener("DOMContentLoaded",function(){const n=document.querySelectorAll(".nav-item"),t=Array.from(n),e=window.location.pathname;t.forEach(t=>{const n=t.querySelector("a");if(n){const s=n.getAttribute("href");(s===e||e==="/"&&s==="/"||s==="/"&&e==="/"||e.startsWith(s)&&s!=="/")&&t.classList.add("active")}}),t.forEach(e=>{e.addEventListener("mouseenter",function(){this.classList.contains("active")||this.classList.add("hover-effect")}),e.addEventListener("mouseleave",function(){this.classList.remove("hover-effect")})})})